// Test that Poll NDF handles all comms errors returned properly, and that it decodes and successfully returns an ndf
func TestPollNdf(t *testing.T) {
	newNdf := &ndf.NetworkDefinition{}

	// Test that poll ndf fails if getHost returns an error
	GetHostErrBool = false
	RequestNdfErr = nil

	_, err := PollNdf(newNdf, mockNotificationComms{})

	if err == nil {
		t.Logf("GetHost should have failed but it didnt't: %+v", err)
		t.Fail()
	}

	// Test that pollNdf returns an error in this case
	RequestNdfErr = errors.New("Permissioning server does not have an ndf to give to client")
	GetHostErrBool = true
	testNdf ,err := PollNdf(newNdf, mockNotificationComms{})

	if err != nil && testNdf != nil {
		t.Logf("RequestNdf should have returned nil for everything because there is no new ndf but didnt")
		t.Fail()
	}

	// Test that pollNdf Fails if it cant decode the request msg
	RequestNdfErr = nil
	GetHostErrBool = true
	NdfToreturn.Ndf = []byte(ExampleBadNdfJSON)
	_, err = PollNdf(newNdf, mockNotificationComms{})

	if err == nil {
		t.Logf("RequestNdf should have failed to parse bad ndf: %+v", err)
		t.Fail()
	}

	// Test that pollNDf Is successful with expected result
	RequestNdfErr = nil
	GetHostErrBool = true
	NdfToreturn.Ndf = []byte(ExampleNdfJSON)
	_, err = PollNdf(newNdf, mockNotificationComms{})

	if err != nil {
		t.Logf("Ndf failed to parse: %+v", err)
		t.Fail()
	}

}

// Mock the Get host function to return
func (m mockNotificationComms) GetHost(hostId string) (*connect.Host, bool) {
	return nil, GetHostErrBool
}

func (m mockNotificationComms) RequestNdf(host *connect.Host, message *pb.NDFHash) (*pb.NDF, error) {
	return &NdfToreturn, RequestNdfErr
}
